import {Divider, Image, message, Row} from "antd";
import {ProFormDateRangePicker, ProFormInstance} from "@ant-design/pro-form";
import {ProFormSelect, ProFormText, StepsForm} from "@ant-design/pro-components";
import React, {useEffect, useRef} from "react";
import styles from "@/pages/club/pages/loader.less";
import {newClubApplication} from "@/services/login/api";
import cookie from "react-cookies";
import {history} from "@@/core/history";




type FormValue = {
  jobInfo: {
    name: string;
    type: string;
  };
  syncTableInfo: {
    title: string;
  };
};
const formValue: FormValue = {
  jobInfo: {
    name: 'normal job',
    type: 1,
  },
  syncTableInfo: {
    title: 'example table title',
  },
};
const waitTime = (time: number = 100) => {
  return new Promise((resolve) => {
    setTimeout(() => {
      resolve(formValue);
    }, time);
  });
};


const Application = () => {
  const formMapRef = useRef<React.MutableRefObject<ProFormInstance<any> | undefined>[]>([]);
  useEffect(() => {
    waitTime(1000).then(() => {
      // 编辑场景下需要使用formMapRef循环设置formData
      formMapRef?.current?.forEach((formInstanceRef) => {
        formInstanceRef?.current?.setFieldsValue(formValue);
      });
    });
  }, []);
  const jobType = [
    {
      value: 1,
      label: '国企',
    },
    {
      value: 2,
      label: '私企',
    },
  ];
  const { location } = history;
  return (
    <div>
        <Row>
          <Image
            className={styles.logo}
            width={100}
            src="https://rootjam.pamalee.cn/assets/logo.c0ec237b.png"
            fallback="data:image/png;base64,"
          />
        </Row>
      <h1>{"Dance Club"}</h1>
      <h3>Welcome to our dance club! We are a community of dancers and music lovers who come together to enjoy the rhythm, beats, and moves of the night. Our dance floor is the perfect place to let go of your worries and immerse yourself in the music. Our goal is to provide a safe, inclusive, and energetic space where everyone can dance and have fun. Whether you're a beginner or a seasoned dancer, you'll find a place here. </h3>
      <Divider>APPLICATION</Divider>
      <StepsForm

        onFinish={(values) => {
          console.log(values);
          const data: API.ClubApplication = {
            token: cookie.load("Ribo_token"),
            cid: location?.query.cid,
            reason: values.reason,
            fee: values.fee,
          }
          newClubApplication(data).then((res) => {
            if (res.code == "200"){
              message.success("Application submitted successfully");
            }else {
              message.error(res.message);
            }
          });

        }}
      >
        <StepsForm.StepForm name="step1" title="JOIN REASON">
          <ProFormText width={"md"}
                       label="REASON"
                       rules={[{ required: true, message: 'Please enter reason' }]}
                       name="reason" />
        </StepsForm.StepForm>
        <StepsForm.StepForm name="step2" title={'FEE'}>
          <ProFormText width={"md"}
                       rules={[{ required: true, message: 'Please enter Fee'}]}
                       label="CLUB FEE" name="fee" />
        </StepsForm.StepForm>
      </StepsForm>
    </div>
  )
}

export default Application;
